import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import './DashboardSelector.css';

function DashboardSelector() {
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  const handleDashboardSelect = (dashboardType) => {
    navigate(`/dashboard/${dashboardType}`);
  };

  return (
    <div className="dashboard-selector-container">
      <div className="welcome-card">
        <h1>¡Bienvenido, {user?.name}!</h1>
        <p>Selecciona el módulo que deseas utilizar</p>
        
        <div className="dashboard-options">
          <button 
            className="dashboard-option"
            onClick={() => handleDashboardSelect('powerbi')}
          >
            <div className="option-icon">📊</div>
            <h3>Dashboard Principal</h3>
            <p>Indicadores y estadísticas</p>
          </button>
          
          {user?.role === 'admin' && (
            <>
              <button 
                className="dashboard-option"
                onClick={() => handleDashboardSelect('admin')}
              >
                <div className="option-icon">👑</div>
                <h3>Administración</h3>
                <p>Gestión de usuarios</p>
              </button>
              
              <button 
                className="dashboard-option"
                onClick={() => handleDashboardSelect('users')}
              >
                <div className="option-icon">👥</div>
                <h3>Usuarios</h3>
                <p>Listado y registros</p>
              </button>
            </>
          )}
        </div>
      </div>
      
      <button 
        onClick={logout}
        className="floating-btn logout"
        title="Cerrar sesión"
      >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white">
          <path d="M10.09 15.59L11.5 17l5-5-5-5-1.41 1.41L12.67 11H3v2h9.67l-2.58 2.59zM19 3H5c-1.11 0-2 .9-2 2v4h2V5h14v14H5v-4H3v4c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2z"/>
        </svg>
      </button>
    </div>
  );
}

export default DashboardSelector;
