import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import './DashboardSelector.css';

function DashboardSelector() {
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  console.log('[DashboardSelector] Datos del usuario:', user);

  const handleDashboardSelect = (dashboardType) => {
    navigate(`/dashboard/${dashboardType}`);
  };

  const getDisplayName = () => {
    if (!user) {
      console.warn('[DashboardSelector] No hay usuario logueado');
      return 'Usuario';
    }
    
    // Verificación completa de datos
    const hasValidName = user.name && typeof user.name === 'string';
    const hasValidLastName = user.lastName && typeof user.lastName === 'string';
    
    if (hasValidName && hasValidLastName) {
      return `${user.name} ${user.lastName}`;
    }
    
    if (hasValidName) {
      return user.name;
    }
    
    if (user.email) {
      return user.email.split('@')[0];
    }
    
    return 'Usuario';
  };

  return (
    <div className="dashboard-selector-container">
      <div className="welcome-card">
        <h1>¡Bienvenido, {getDisplayName()}!</h1>
        <p>Selecciona el dashboard que deseas visualizar</p>
        
        <div className="dashboard-options">
          <button 
            className="dashboard-option"
            onClick={() => handleDashboardSelect('powerbi')}
          >
            <div className="option-icon">📊</div>
            <h3>Dashboard Power BI</h3>
            <p>Visualización de datos avanzados</p>
          </button>
          
          {user?.role === 'admin' && (
            <button 
              className="dashboard-option"
              onClick={() => handleDashboardSelect('admin')}
            >
              <div className="option-icon">👑</div>
              <h3>Panel de Administración</h3>
              <p>Gestión de usuarios y accesos</p>
            </button>
          )}
          
          <button 
            className="dashboard-option"
            onClick={() => handleDashboardSelect('profile')}
          >
            <div className="option-icon">👤</div>
            <h3>Mi Perfil</h3>
            <p>Tu información personal</p>
          </button>
        </div>
      </div>
      
      <button 
        className="logout-btn" 
        onClick={logout}
        aria-label="Cerrar sesión"
      >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white" width="24px" height="24px">
          <path d="M0 0h24v24H0z" fill="none"/>
          <path d="M10.09 15.59L11.5 17l5-5-5-5-1.41 1.41L12.67 11H3v2h9.67l-2.58 2.59zM19 3H5c-1.11 0-2 .9-2 2v4h2V5h14v14H5v-4H3v4c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2z"/>
        </svg>
      </button>
    </div>
  );
}

export default DashboardSelector;
