import React, { createContext, useState, useEffect, useContext } from 'react';
import { 
  auth,
  db,
  doc,
  setDoc,
  getDoc,
  signInWithEmailAndPassword,
  signOut,
  createUserWithEmailAndPassword,
  onAuthStateChanged
} from '../firebase/config';

const AuthContext = createContext();

export function AuthProvider({ children }) {

  const [user, setUser] = useState(null);
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

const fetchUserData = async (uid) => {
  try {
    const userDoc = await getDoc(doc(db, 'users', uid));
    if (userDoc.exists()) {
      const data = userDoc.data();
      return {
        name: data.name || '',
        lastName: data.lastName || '',
        role: data.role || 'user'
      };
    }
    return null;
  } catch (error) {
    console.error("Error fetching user data:", error);
    return null;
  }
};

const login = async (email, password) => {
  try {
    const userCredential = await signInWithEmailAndPassword(auth, email, password);
    const additionalData = await fetchUserData(userCredential.user.uid);
    
    if (!additionalData) {
      throw new Error("No se encontraron datos adicionales del usuario");
    }

    return { 
      success: true, 
      user: {
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        name: additionalData.name,
        lastName: additionalData.lastName,
        role: additionalData.role
      } 
    };
  } catch (error) {
    console.error("Login error:", error);
    return { success: false, error: error.message };
  }
};

  const logout = async () => {
    try {
      await signOut(auth);
      window.location.href = '/';
    } catch (error) {
      console.error("Error al cerrar sesión:", error);
    }
  };
const register = async (email, password, userData) => {
  try {
    const userCredential = await createUserWithEmailAndPassword(auth, email, password);
    
    await setDoc(doc(db, 'users', userCredential.user.uid), {
      email: email,
      name: userData.name,
      lastName: userData.lastName,
      role: userData.role || 'user',
      createdAt: new Date().toISOString()
    });

    // Verificación después de guardar
    const savedData = await getDoc(doc(db, 'users', userCredential.user.uid));
    console.log("Datos guardados:", savedData.data());

    return { 
      success: true, 
      user: {
        uid: userCredential.user.uid,
        email: email,
        name: userData.name,
        lastName: userData.lastName,
        role: userData.role || 'user'
      } 
    };
  } catch (error) {
    console.error("Registration error:", error);
    return { success: false, error: error.message };
  }
};

useEffect(() => {
  const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
    if (currentUser) {
      const additionalData = await fetchUserData(currentUser.uid);
      setUser({
        ...currentUser,
        name: additionalData?.name || '',
        lastName: additionalData?.lastName || '',
        role: additionalData?.role || 'user'
      });
      setUserData(additionalData);
    } else {
      setUser(null);
      setUserData(null);
    }
    setLoading(false);
  });
  return () => unsubscribe();
}, []);

  const value = {
    user,
    userData,
    loading,
    login,
    logout,
    register,
    isAuthenticated: !!user,
    isAdmin: user?.role === 'admin'
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
