import React, { useState, useEffect, createContext, useContext } from 'react'; // Added useContext here
import { auth, db } from '../firebase/config';
import { 
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged
} from 'firebase/auth';
import { doc, setDoc, getDoc, serverTimestamp } from 'firebase/firestore';

// Creamos y exportamos el contexto
export const AuthContext = createContext();

// Hook personalizado para usar el contexto
export function useAuth() {
  return useContext(AuthContext); // Ahora useContext est치 definido
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null);
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Registrar nuevo usuario (solo para admins)
  const register = async (email, password, additionalData) => {
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      
      await setDoc(doc(db, "users", userCredential.user.uid), {
        ...additionalData,
        email,
        createdAt: serverTimestamp(),
        lastLogin: null,
        role: additionalData.role || 'user'
      });
      
      return userCredential.user;
    } catch (error) {
      throw error;
    }
  };

  // ... (resto del c칩digo permanece igual)
  
  return (
    <AuthContext.Provider value={{
      currentUser,
      userData,
      loading,
      register,
      login,
      logout,
      isAdmin
    }}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
