import { useAuth } from '../auth/AuthContext';
import { Navigate } from 'react-router-dom';

export default function AdminRoute({ children }) {
  const { currentUser, isAdmin } = useAuth();
  
  if (!currentUser) return <Navigate to="/login" />; // Si no está logueado → Login
  if (!isAdmin()) return <Navigate to="/dashboard" />; // Si no es admin → Dashboard
  
  return children; // Si es admin → Permite acceso
}
