import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { db } from '../firebase/config';
import { collection, query, where, onSnapshot } from 'firebase/firestore';


function UserLogins() {
  
  const navigate = useNavigate();
  const { username } = useParams();
  const [logins, setLogins] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const q = query(
      collection(db, "logins"), 
      where("username", "==", username)
    );
    
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const loginData = [];
      querySnapshot.forEach((doc) => {
        loginData.push({ 
          id: doc.id, 
          ...doc.data(),
          timestamp: doc.data().timestamp?.toDate() 
        });
      });
      setLogins(loginData.sort((a, b) => b.timestamp - a.timestamp));
      setLoading(false);
    });

    return () => unsubscribe();
  }, [username]);

  return (
    <div className="dashboard-container">
      <button 
        onClick={() => navigate('/dashboard/admin/users')} // Volver a lista de usuarios
        className="back-btn"
      >
        &larr; Volver a la lista
      </button>

      <h1>Historial de Accesos: {username}</h1>
      
    
      <button onClick={() => navigate('/dashboard/users')} className="back-btn">
        Volver a la lista
      </button>

    </div>
  );
}

export default UserLogins;
