import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { db } from '../firebase/config';
import { collection, query, where, onSnapshot } from 'firebase/firestore';


function UserLogins() {
  const { username } = useParams();
  const navigate = useNavigate();
  const [logins, setLogins] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const q = query(
      collection(db, "logins"), 
      where("username", "==", username)
    );
    
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const loginData = [];
      querySnapshot.forEach((doc) => {
        loginData.push({ 
          id: doc.id, 
          ...doc.data(),
          timestamp: doc.data().timestamp?.toDate() 
        });
      });
      setLogins(loginData.sort((a, b) => b.timestamp - a.timestamp));
      setLoading(false);
    });

    return () => unsubscribe();
  }, [username]);

  return (
    <div className="dashboard-container">
      <h1>Historial de Accesos: {username}</h1>
      
      {loading ? (
        <p>Cargando historial...</p>
      ) : (
        <>
          {logins.length === 0 ? (
            <p>No hay registros de acceso para este usuario.</p>
          ) : (
            <table className="user-table">
              <thead>
                <tr>
                  <th>Fecha y Hora</th>
                  <th>Usuario</th>
                </tr>
              </thead>
              <tbody>
                {logins.map(login => (
                  <tr key={login.id}>
                    <td>{login.timestamp?.toLocaleString()}</td>
                    <td>{login.username}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
      
      <button onClick={() => navigate('/dashboard/users')} className="back-btn">
        Volver a la lista
      </button>
    </div>
  );
}

export default UserLogins;
