import React, { useEffect, useRef } from 'react';
import { useAuth } from '../auth/AuthContext';
import './Dashboard.css';

function Dashboard() {
  const { user, logout, loading } = useAuth();
  const iframeRef = useRef(null);

  useEffect(() => {
    const hidePowerBIFooter = () => {
      const iframe = iframeRef.current;
      if (iframe) {
        // Espera a que el iframe cargue
        iframe.onload = () => {
          try {
            // Accede al documento dentro del iframe
            const iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
            
            // Oculta elementos específicos del footer de Power BI
            const footer = iframeDoc.querySelector('.footer');
            const navigationPane = iframeDoc.querySelector('.navContainer');
            
            if (footer) footer.style.display = 'none';
            if (navigationPane) navigationPane.style.display = 'none';
            
            // Ajuste adicional para el contenedor principal
            const mainContainer = iframeDoc.querySelector('.mainContainer');
            if (mainContainer) mainContainer.style.height = '100%';
          } catch (error) {
            console.log("No se pudo ocultar el footer:", error);
          }
        };
      }
    };

    hidePowerBIFooter();
  }, []);

  if (loading) return <div className="loading-screen">Cargando...</div>;
  if (!user) return null;

  return (
    <div className="dashboard-container">
      <button 
        onClick={logout} 
        className="logout-btn"
        title="Cerrar sesión"
      >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white" width="24px" height="24px">
          <path d="M0 0h24v24H0z" fill="none"/>
          <path d="M10.09 15.59L11.5 17l5-5-5-5-1.41 1.41L12.67 11H3v2h9.67l-2.58 2.59zM19 3H5c-1.11 0-2 .9-2 2v4h2V5h14v14H5v-4H3v4c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2z"/>
        </svg>
      </button>

      <div className="powerbi-embed-container">
           <iframe title="APS NACIONAL RED SALUD 1499" 
      src="https://app.powerbi.com/view?r=eyJrIjoiZTVlOTI3ZTQtMmZmNy00MThkLTljZjItYzZmNTBkNDc5ZjdjIiwidCI6ImE2NjdiNTIwLTBjZmMtNDViOC1hNTkwLWFiZjNmMjJkNWI3YSJ9&pageName=90e5b7e0ed800c4dd396" f
      allowFullScreen="true"></iframe>
      </div>
    </div>
  );

  return (
  <div className="dashboard-container">
    <div className="powerbi-embed-container">
      <iframe 
        ref={iframeRef}
        title="APS NACIONAL RED SALUD 1499" 
        src="https://app.powerbi.com/view?r=eyJrIjoiZTVlOTI3ZTQtMmZmNy00MThkLTljZjItYzZmNTBkNDc5ZjdjIiwidCI6ImE2NjdiNTIwLTBjZmMtNDViOC1hNTkwLWFiZjNmMjJkNWI3YSJ9&pageName=90e5b7e0ed800c4dd396"
        allowFullScreen="true"
      ></iframe>
    </div>
  </div>
);
}

export default Dashboard;
