import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import { authService } from '../auth/authService';

function Login() {
  // Definimos todas las variables de estado y hooks
  const navigate = useNavigate();
  const { users, login } = useAuth();
  
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  
  const [message, setMessage] = useState({
    text: '',
    isError: false
  });
  
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    try {
      const result = await authService.login(
        formData.username, 
        formData.password,
        users
      );
      
      if (result?.success) {
        const role = login(result.data);
        
        if (role) {
          navigate(role === 'admin' ? '/dashboard/create-user' : '/dashboard');
        } else {
          setMessage({
            text: 'Error al iniciar sesi칩n',
            isError: true
          });
        }
      } else {
        setMessage({
          text: result?.message || 'Credenciales incorrectas',
          isError: true
        });
      }
    } catch (error) {
      setMessage({
        text: 'Error al conectar con el servidor',
        isError: true
      });
    }
    
    setIsSubmitting(false);
  };

  return (
    <div className="login-container">
      <h1>Iniciar Sesi칩n</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Usuario:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Contrase침a:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <button 
          type="submit" 
          className="login-btn"
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Iniciando...' : 'Ingresar'}
        </button>
      </form>
      {message.text && (
        <div className={`message ${message.isError ? 'error' : 'success'}`}>
          {message.text}
        </div>
      )}
    </div>
  );
}

export default Login;
