import React from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import './AdminPanel.css';

function AdminPanel() {
  const { user } = useAuth();
  const navigate = useNavigate();

  return (
    <div className="admin-container">
      <button 
        onClick={() => navigate('/dashboard')}
        className="floating-btn back"
        title="Volver"
      >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
          <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z"/>
        </svg>
      </button>

      <div className="admin-content">
        <h1>Panel de Administraci√≥n</h1>
        <p className="welcome-message">Bienvenido, <strong>{user?.name}</strong> (Rol: {user?.role})</p>
        
        <nav className="admin-nav">
          <Link to="/dashboard/admin/users" className="nav-link">
            <span>üë•</span> Gesti√≥n de Usuarios
          </Link>
          <Link to="/dashboard/admin/create-user" className="nav-link">
            <span>‚ûï</span> Crear Nuevo Usuario
          </Link>
          <Link to="/dashboard/admin/logs" className="nav-link">
            <span>üìù</span> Registros de Acceso
          </Link>
        </nav>
        
        <div className="admin-outlet">
          <Outlet />
        </div>
      </div>
    </div>
  );
}

export default AdminPanel;
