import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import './CreateUser.css'; // Estilos específicos

function CreateUser() {
  const navigate = useNavigate();
  const { register, user } = useAuth();
  
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    isAdmin: false
  });
  
  const [message, setMessage] = useState({
    text: '',
    isError: false
  });
  
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Validación básica
    if (formData.password.length < 6) {
      setMessage({
        text: 'La contraseña debe tener al menos 6 caracteres',
        isError: true
      });
      setIsSubmitting(false);
      return;
    }

    const result = await register(formData.email, formData.password, formData.isAdmin);
    
    if (result.success) {
      setMessage({
        text: `Usuario ${formData.email} creado exitosamente como ${formData.isAdmin ? 'Administrador' : 'Usuario normal'}`,
        isError: false
      });
      // Limpiar formulario
      setFormData({ email: '', password: '', isAdmin: false });
    } else {
      setMessage({
        text: result.error || 'Error al crear usuario',
        isError: true
      });
    }
    
    setIsSubmitting(false);
  };

  // Si el usuario no es admin, redirigir
  if (user?.email !== 'admin@example.com') {
    navigate('/dashboard');
    return null;
  }

  return (
    <div className="create-user-container">
      <h1>Crear Nuevo Usuario</h1>
      <p className="admin-welcome">Bienvenido, Administrador {user.email}</p>
      
      <form onSubmit={handleSubmit} className="create-user-form">
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
            placeholder="ejemplo@dominio.com"
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="password">Contraseña (mínimo 6 caracteres):</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
            minLength="6"
            placeholder="••••••"
          />
        </div>
        
        <div className="form-group checkbox-group">
          <label>
            <input
              type="checkbox"
              checked={formData.isAdmin}
              onChange={(e) => setFormData({...formData, isAdmin: e.target.checked})}
              className="admin-checkbox"
            />
            <span>¿Dar permisos de administrador?</span>
          </label>
        </div>
        
        <div className="form-actions">
          <button 
            type="submit" 
            className="submit-btn"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="spinner"></span> Creando...
              </>
            ) : (
              'Crear Usuario'
            )}
          </button>
          
          <button 
            type="button" 
            onClick={() => navigate('/dashboard')}
            className="cancel-btn"
          >
            Volver al Dashboard
          </button>
        </div>
      </form>
      
      {message.text && (
        <div className={`message ${message.isError ? 'error' : 'success'}`}>
          {message.text}
        </div>
      )}
    </div>
  );
}

export default CreateUser;
