import React from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom'; // Añadir useNavigate
import { useAuth } from '../auth/AuthContext';

function AdminPanel() {
  const { user } = useAuth();
  const navigate = useNavigate(); // Añadir este hook

  return (
    <div className="admin-container">
      <button 
        onClick={() => navigate('/dashboard')}
        className="back-btn"
      >
        &larr; Volver al menú
      </button>
      
      <h1>Panel de Administración</h1>
      <p>Bienvenido, {user?.name} (Admin)</p>
      
      <nav className="admin-nav">
        <Link to="/dashboard/admin/users">Lista de Usuarios</Link>
        <Link to="/dashboard/admin/create-user">Crear Usuario</Link>
      </nav>
      
      <div className="admin-content">
        <Outlet />
      </div>
    </div>
  );
}

export default AdminPanel;
