import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';

function Dashboard() {
  const { user, logout, isAdmin } = useAuth();
  const navigate = useNavigate();

  // Redirigir si no hay usuario
  React.useEffect(() => {
    if (!user) {
      navigate('/');
    }
  }, [user, navigate]);

  // Mostrar carga mientras se verifica el usuario
  if (!user) {
    return <div className="dashboard-container">Cargando...</div>;
  }

  return (
    <div className="dashboard-container">
      <h1>Bienvenido, {user?.name || 'Usuario'}</h1>
      
      <div className="user-info">
        <p><strong>Usuario:</strong> {user?.username || 'No disponible'}</p>
        <p><strong>Rol:</strong> {user?.role || 'user'}</p>
        <p><strong>Último acceso:</strong> {user?.lastLogin ? 
          new Date(user.lastLogin).toLocaleString() : 'Nunca'}</p>
      </div>

      {isAdmin() && (
        <div className="admin-actions">
          <h2>Acciones de Administrador</h2>
          <button 
            onClick={() => navigate('/dashboard/users')} 
            className="admin-btn"
          >
            Ver Todos los Usuarios
          </button>
          <button 
            onClick={() => navigate('/dashboard/create-user')} 
            className="admin-btn"
          >
            Crear Nuevo Usuario
          </button>
        </div>
      )}
      
      <button onClick={logout} className="logout-btn">Cerrar Sesión</button>
    </div>
  );
}

export default Dashboard;
