import { useAuth } from '../auth/AuthContext';
import { Navigate } from 'react-router-dom';

export default function PrivateRoute({ children }) {
  const { currentUser, loading } = useAuth();

  if (loading) {
    return <div className="loading-screen">Cargando...</div>;
  }

  if (!currentUser) {
    return <Navigate to="/" />;
  }

  return children;
}
