const handleSubmit = async (e) => {
  e.preventDefault();
  setIsSubmitting(true);
  
  try {
    const result = await authService.login(
      formData.username, 
      formData.password,
      users
    );
    
    if (result?.success) {
      const role = login(result.data);
      
      if (role) {
        navigate(role === 'admin' ? '/dashboard/create-user' : '/dashboard');
      } else {
        setMessage({
          text: 'Error al iniciar sesión',
          isError: true
        });
      }
    } else {
      setMessage({
        text: result?.message || 'Credenciales incorrectas',
        isError: true
      });
    }
  } catch (error) {
    setMessage({
      text: 'Error al conectar con el servidor',
      isError: true
    });
  }
  
  setIsSubmitting(false);
};
