import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import { db } from '../firebase/config';
import { collection, query, where, onSnapshot } from 'firebase/firestore';

function UserList() {
  const { users } = useAuth();
  const navigate = useNavigate();
  const [logins, setLogins] = useState([]);

  useEffect(() => {
    // Escuchar cambios en la colección de logins
    const q = query(collection(db, "logins"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const loginData = [];
      querySnapshot.forEach((doc) => {
        loginData.push({ id: doc.id, ...doc.data() });
      });
      setLogins(loginData);
    });

    return () => unsubscribe();
  }, []);

  const getUserLogins = (username) => {
    return logins
      .filter(login => login.username === username)
      .sort((a, b) => b.timestamp?.seconds - a.timestamp?.seconds);
  };

  return (
    <div className="dashboard-container">
      <h1>Usuarios Registrados</h1>
      
      <table className="user-table">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Usuario</th>
            <th>Rol</th>
            <th>Último Login</th>
            <th>Historial</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => {
            const userLogins = getUserLogins(user.username);
            const lastLogin = userLogins[0]?.timestamp?.toDate()?.toLocaleString();
            
            return (
              <tr key={user.username}>
                <td>{user.name}</td>
                <td>{user.username}</td>
                <td>{user.role}</td>
                <td>{lastLogin || 'Nunca'}</td>
                <td>
                  <button 
                    onClick={() => navigate(`/dashboard/user-logins/${user.username}`)}
                    className="detail-btn"
                  >
                    Ver
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      
      <button onClick={() => navigate('/dashboard')} className="back-btn">
        Volver al Dashboard
      </button>
    </div>
  );
}

export default UserList;
