import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import './DashboardSelector.css';

function DashboardSelector() {
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  const handleDashboardSelect = (dashboardType) => {
    navigate(`/dashboard/${dashboardType}`);
  };

  const getDisplayName = () => {
    if (!user) return 'Usuario';
    
    // Mostrar nombre completo si existe
    if (user.name && user.lastName) {
      return `${user.name} ${user.lastName}`.trim();
    }
    
    // Mostrar nombre si solo existe nombre
    if (user.name) return user.name;
    
    // Mostrar primera parte del email como fallback
    return user.email ? user.email.split('@')[0] : 'Usuario';
  };

  return (
    <div className="dashboard-selector-container">
      <div className="welcome-card">
        <h1>¡Bienvenido, {getDisplayName()}!</h1>
        <p>Selecciona el dashboard que deseas visualizar</p>
        
        <div className="dashboard-options">
          <button 
            className="dashboard-option"
            onClick={() => handleDashboardSelect('powerbi')}
          >
            <div className="option-icon">📊</div>
            <h3>Dashboard Power BI</h3>
            <p>Visualización de datos avanzados</p>
          </button>
          
          {user?.role === 'admin' && (
            <button 
              className="dashboard-option"
              onClick={() => handleDashboardSelect('admin')}
            >
              <div className="option-icon">👑</div>
              <h3>Panel de Administración</h3>
              <p>Gestión de usuarios y accesos</p>
            </button>
          )}
          
          <button 
            className="dashboard-option"
            onClick={() => handleDashboardSelect('profile')}
          >
            <div className="option-icon">👤</div>
            <h3>Mi Perfil</h3>
            <p>Tu información personal</p>
          </button>
        </div>
      </div>
      
      <button className="logout-btn" onClick={logout}>
        Cerrar Sesión
      </button>
    </div>
  );
}

export default DashboardSelector;
