import React, { createContext, useState, useContext, useEffect } from 'react';
import { loginService } from './loginService';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState([
    { username: "admin", password: "12345", name: "Administrador", role: "admin" },
    { username: "user1", password: "54321", name: "Usuario Demo", role: "user" }
  ]);

  const login = async (userData) => {
    // Registrar el login en Firebase
    await loginService.recordLogin(userData.username);
    
    const updatedUser = {
      ...userData,
      lastLogin: new Date().toISOString()
    };
    
    setUsers(prevUsers => 
      prevUsers.map(u => 
        u.username === userData.username ? updatedUser : u
      )
    );
    
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const isAuthenticated = () => {
    return user !== null || localStorage.getItem('user') !== null;
  };

  const isAdmin = () => {
    return user?.role === 'admin';
  };

  const createUser = (newUser) => {
    setUsers(prevUsers => [...prevUsers, {
      ...newUser,
      role: 'user',
      lastLogin: null
    }]);
    return { success: true };
  };

  // Cargar usuario desde localStorage al iniciar
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  return (
    <AuthContext.Provider value={{ 
      user, 
      users,
      login, 
      logout, 
      isAuthenticated,
      isAdmin,
      createUser
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
