import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import './DashboardSelector.css';

function DashboardSelector() {
  const navigate = useNavigate();
  const { user, userData, logout } = useAuth();
  
  const handleDashboardSelect = (dashboardType) => {
    navigate(`/dashboard/${dashboardType}`);
  };

  // Función para mostrar el nombre de bienvenida
  const getWelcomeName = () => {
    if (userData?.firstName && userData?.lastName) {
      return `${userData.firstName} ${userData.lastName}`;
    }
    return user?.email || 'Usuario';
  };

  return (
    <div className="dashboard-selector-container">
      <div className="welcome-card">
        <h1 className="welcome-title">¡Bienvenido, <span className="user-name">{getWelcomeName()}</span>!</h1>
        <p className="welcome-subtitle">Selecciona el dashboard que deseas visualizar</p>
        
        <div className="dashboard-options">
          {/* ... tus opciones de dashboard existentes ... */}
        </div>
      </div>
      
      <button className="logout-btn" onClick={logout}>
        Cerrar Sesión
      </button>
    </div>
  );
}

export default DashboardSelector;
